import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../hooks/useAuth';
import { useChat } from '../hooks/useChat';
import ChatList from '../components/Chat/ChatList';
import ChatBox from '../components/Chat/ChatBox';
import UserAvatar from '../components/UserAvatar/UserAvatar';
import GroupChatModal from '../components/Chat/Modals/GroupChatModal';
import ProfileModal from '../components/Chat/Modals/ProfileModal';
import NotificationBadge from '../components/Chat/Notifications/NotificationBadge';
import NotificationDropdown from '../components/Chat/Notifications/NotificationDropdown';
import socket from '../utils/socket';
import './ChatPage.css';

const ChatPage = () => {
  const { user, logout } = useAuth();
  const { 
    chats, 
    selectedChat, 
    setSelectedChat, 
    fetchChats, 
    accessChat,
    notifications,
    removeNotification,
    addNotification,
    error: chatError
  } = useChat();

  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [showGroupModal, setShowGroupModal] = useState(false);
  const [showProfileModal, setShowProfileModal] = useState(false);
  const [showNotifications, setShowNotifications] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    if (!user) {
      navigate('/login');
      return;
    }

    // Connect to socket
    socket.emit('setup', user);

    // Fetch chats
    fetchChats(user.token);

    // Socket listener for new message notification
    const handleNewMessage = (newMessage) => {
      // If chat is not selected or doesn't match current chat
      if (!selectedChat || selectedChat._id !== newMessage.chat._id) {
        // Add notification
        addNotification({
          ...newMessage,
          createdAt: new Date(),
          sender: newMessage.sender,
          chat: newMessage.chat
        });
      }
    };

    socket.on('message received', handleNewMessage);

    return () => {
      socket.off('message received', handleNewMessage);
    };
  }, [user, navigate, fetchChats, selectedChat, addNotification]);

  // Display chat error if present
  useEffect(() => {
    if (chatError) {
      setError(chatError);
    }
  }, [chatError]);

  const handleSearchUsers = useCallback(async () => {
    if (!searchTerm) {
      setSearchResults([]);
      return;
    }

    try {
      setLoading(true);
      setError(null);

      const response = await fetch(`http://localhost:5000/api/users?search=${searchTerm}`, {
        method: 'GET',
        headers: {
          Authorization: `Bearer ${user.token}`,
        },
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || 'Failed to search users');
      }

      setSearchResults(data);
      setLoading(false);
    } catch (error) {
      setError('Error searching users: ' + error.message);
      setLoading(false);
    }
  }, [searchTerm, user?.token]);

  const handleUserSelect = useCallback(async (userId) => {
    try {
      setError(null);
      await accessChat(userId, user.token);
      setSearchTerm('');
      setSearchResults([]);
    } catch (error) {
      setError('Error starting chat: ' + error.message);
    }
  }, [accessChat, user?.token]);

  const handleChatSelect = useCallback((chat) => {
    setSelectedChat(chat);

    // Remove notifications for this chat
    if (notifications.some(n => n.chat._id === chat._id)) {
      removeNotification(chat._id);
    }
  }, [setSelectedChat, notifications, removeNotification]);

  const handleLogout = useCallback(() => {
    logout();
    navigate('/login');
  }, [logout, navigate]);

  // Memoize filtered chats for better performance
  const filteredChats = useMemo(() => {
    return chats;
  }, [chats]);

  return (
    <div className="chat-page">
      <div className="sidebar">
        <div className="sidebar-header">
          <div className="user-info" onClick={() => setShowProfileModal(true)}>
            <UserAvatar user={user} />
            <span className="username">{user?.username}</span>
          </div>
          <div className="header-actions">
            <NotificationBadge onClick={() => setShowNotifications(!showNotifications)} />
            <button className="logout-btn" onClick={handleLogout}>
              Logout
            </button>
          </div>
        </div>

        {error && (
          <div className="error-message">
            {error}
            <button onClick={() => setError(null)}>Ã—</button>
          </div>
        )}

        <div className="search-container">
          <input
            type="text"
            placeholder="Search users..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleSearchUsers()}
          />
          <button onClick={handleSearchUsers} disabled={loading}>
            {loading ? <span className="spinner-small"></span> : <i className="fas fa-search"></i>}
          </button>
        </div>

        {searchResults.length > 0 && (
          <div className="search-results">
            {searchResults.map(user => (
              <div
                key={user._id}
                className="search-user-item"
                onClick={() => handleUserSelect(user._id)}
              >
                <UserAvatar user={user} />
                <div className="user-details">
                  <div className="user-name">{user.username}</div>
                  <div className="user-email">{user.email}</div>
                </div>
              </div>
            ))}
          </div>
        )}

        <div className="sidebar-actions">
          <button 
            className="create-group-btn"
            onClick={() => setShowGroupModal(true)}
          >
            <i className="fas fa-plus"></i> Create Group Chat
          </button>
        </div>

        <ChatList
          chats={filteredChats}
          handleChatSelect={handleChatSelect}
        />
      </div>

      <div className="chat-container">
        <ChatBox chat={selectedChat} />
      </div>

      {/* Modals */}
      <GroupChatModal 
        isOpen={showGroupModal} 
        onClose={() => setShowGroupModal(false)} 
      />

      <ProfileModal 
        isOpen={showProfileModal} 
        onClose={() => setShowProfileModal(false)} 
        profile={user}
      />

      {/* Notification Dropdown */}
      <NotificationDropdown 
        isOpen={showNotifications} 
        onClose={() => setShowNotifications(false)} 
      />
    </div>
  );
};

export default ChatPage;
